import "./style.css";
import React, { useRef, useState } from "react";
import { useClickOutside } from "../../helpers/useClickOutside";
import Picker from "emoji-picker-react";
import ImagePreview from "./ImagePreview";
import EmojiPickerBackground from "./EmojiPickerBackground";

export default function CreatePostPopup({ setCreatePostPopup, user }) {
  const [text, setText] = useState("");
  const [showPrev, setShowPrev] = useState(false);
  const [picker, setPicker] = useState(false);
  const popupRef = useRef();
  const textRef = useRef(null);
  useClickOutside(popupRef, () => setCreatePostPopup(false));
  function handleEmoji(e, { emoji }) {
    const ref = textRef.current;
    ref.focus();
  }

  return (
    <div className="blur">
      <div className="post_box" ref={popupRef}>
        <div className="box_header">
          <div
            className="small_circle"
            onClick={() => setCreatePostPopup(false)}
          >
            <i className="exit_icon"></i>
          </div>
          <span>Create Post</span>
        </div>
        {!showPrev && (
          <div className="flex_center">
            <textarea
              ref={textRef}
              maxLength="100"
              value={text}
              placeholder={`What's on your mind, ${user.first_name}`}
              className="post_input"
              onChange={(e) => setText(e.target.value)}
            ></textarea>
          </div>
        )}
        <div className="post_emojis_wrap">
          {picker && (
            <div className="comment_emoji_picker rlmove">
              <Picker onEmojiClick={handleEmoji} />
            </div>
          )}
          <img src="../../../icons/colorful.png" alt="" />
          <i
            className="emoji_icon_large"
            onClick={() => {
              setPicker((prev) => !prev);
            }}
          ></i>
        </div>
      </div>
    </div>
  );
}
