import "./style.css";
import React, { useRef, useState } from "react";
import { useClickOutside } from "../../helpers/useClickOutside";
import ImagePreview from "./ImagePreview";
import EmojiPickerBackgrounds from "./EmojiPickerBackgrounds";
import { Photo } from "../../svg";
import PulseLoader from "react-spinners/PulseLoader";
import { createPost } from "../../functions/post";

export default function CreatePostPopup({ setCreatePostPopup, user }) {
  const [text, setText] = useState("");
  const [showPrev, setShowPrev] = useState(false);
  const [images, setImages] = useState([]);
  const [background, setBackground] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  const popupRef = useRef(null);
  useClickOutside(popupRef, () => setCreatePostPopup(false));

  const postSubmit = async () => {
    if (background) {
      setLoading(true);
      const res = await createPost(
        null,
        background,
        text,
        null,
        user.id,
        user.token
      );
      setLoading(false);
      if (res === "ok") {
        setText("");
        setBackground("");
        setCreatePostPopup(false);
      } else {
        setError(true);
      }
    }
  };

  return (
    <div className="blur">
      <div className="post_box" ref={popupRef}>
        <div className="box_header">
          <div
            className="small_circle"
            onClick={() => setCreatePostPopup(false)}
          >
            <i className="exit_icon"></i>
          </div>
          <span>Create Post</span>
        </div>
        {!showPrev ? (
          <>
            <EmojiPickerBackgrounds
              user={user}
              text={text}
              setText={setText}
              showPrev={showPrev}
              background={background}
              setBackground={setBackground}
            />
          </>
        ) : (
          <ImagePreview
            user={user}
            text={text}
            setText={setText}
            images={images}
            setImages={setImages}
            setShowPrev={setShowPrev}
          />
        )}
        <div
          onClick={() => {
            setShowPrev((prev) => !prev);
          }}
          className="addtoyourpost"
        >
          <div className="post_header_right hover1">
            <Photo color="#45bd62" />
          </div>
        </div>
        <button
          onClick={() => {
            postSubmit();
          }}
          disabled={loading}
          className="post_button"
        >
          {loading ? <PulseLoader color="#fff" size={5} /> : "Post"}
        </button>
      </div>
    </div>
  );
}
