import { useEffect, useReducer, useState } from "react";
import { useSelector } from "react-redux";
import { chatContactsReducer } from "../../../functions/reducers";
import BounceLoader from "react-spinners/BounceLoader";
import "./style.css";
import { getAllConversations } from "../../../functions/chat";
const contactss = [
  { name: "Ashik" },
  { name: "Rashid" },
  { name: "Jebin" },
  { name: "Hanna" },
];
let typingTimer;
const interval = 3000;
export default function ContactBox() {
  let { user } = useSelector((state) => ({ ...state }));
  const [{ loading, error, contacts }, dispatch] = useReducer(
    chatContactsReducer,
    {
      loading: false,
      error: "",
      contacts: [],
    }
  );
  const conversationFetching = async () => {
    dispatch({ type: "CHAT_CONTACTS_REQUEST" });
    const { data, status } = await getAllConversations(user.token);
    if (status === "ok") {
      dispatch({ type: "CHAT_CONTACTS_SUCCESS", payload: data });
    } else {
      dispatch({ type: "CHAT_CONTACTS_SUCCESS" });
    }
  };
  useEffect(() => {
    conversationFetching();
  }, []);
  const [chatSearch, setChatSearch] = useState("");
  setTimeout(() => {}, 3000);
  return (
    <div className="contactBox">
      <div className="wrapper">
        <div className="header">
          <div className="search">
            <input
              onChange={(e) => setChatSearch(e.target.value)}
              type="text"
              value={chatSearch}
              placeholder="Search buddies..."
              onKeyDown={() => {
                clearTimeout(typingTimer);
              }}
              onKeyUp={() => {
                clearTimeout(typingTimer);
                typingTimer = setTimeout(() => {
                  console.log("timerrr");
                }, interval);
              }}
            />
          </div>
          {/* <h3>MOMENTS CHAT</h3> */}
        </div>
        <div className="chat_body scrollbar">
          {loading && (
            <div className="contacts_loading">
              <BounceLoader />
            </div>
          )}
          {!loading &&
            contacts?.map((contact, i) => (
              <div key={i} className="contact">
                <img src={user.picture} alt="" />
                <span>{`${user.first_name} ${user.last_name}`}</span>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
}
