import "./style.css";
import { useSelector } from "react-redux";
import { useEffect, useReducer, useRef, useState } from "react";
import { getAllMessages, messenger } from "../../../functions/chat";
import PuffLoader from "react-spinners/PuffLoader";
import { messagesReducer } from "../../../functions/reducers";
import { io } from "socket.io-client";

export default function ChatBox({ selectedChat }) {
  const { user } = useSelector((state) => ({ ...state }));
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [newMessage, setNewMessage] = useState(false);
  const socket = useRef();
  const [{ messagesLoading, messages, error }, dispatch] = useReducer(
    messagesReducer,
    {
      messagesLoading: false,
      messages: [],
      error: false,
    }
  );
  useEffect(() => {
    if (selectedChat.id) {
    }
  }, [selectedChat]);

  const messageContainerRef = useRef(null);

  const sendMessage = async () => {
    setLoading(true);
    await messenger(selectedChat.id, message, user.token);
    setNewMessage((prev) => !prev);
    setLoading(false);
  };
  const getMessages = async () => {
    try {
      dispatch({ type: "MESSAGES_REQUEST" });
      const { data, status } = await getAllMessages(
        selectedChat.id,
        user.token
      );
      if (status === "ok") {
        dispatch({ type: "MESSAGES_SUCCESS", payload: data });
      }
    } catch (error) {
      dispatch({ type: "MESSAGES_ERROR" });
    }
  };
  useEffect(() => {
    getMessages();
  }, [selectedChat]);
  useEffect(() => {
    getMessages();
  }, [newMessage]);
  // useEffect(() => {
  //   messageContainerRef.current.scrollTop =
  //     messageContainerRef.current.scrollHeight;
  // }, []);
  return (
    <div className="chatBox">
      {!selectedChat.id && (
        <div className="noSelectedChat">
          <div>Select a chat to start messaging</div>
        </div>
      )}
      {selectedChat.id && (
        <div className="chat_box_wrap">
          <div className="chat_box_header">
            <img src={selectedChat.picture} alt="" />
            <h4>{`${selectedChat.first_name} ${selectedChat.last_name}`}</h4>
            <div>online</div>
          </div>
          <div
            ref={messageContainerRef}
            className="message_container scrollbar"
          >
            {!messagesLoading &&
              messages?.map((messageObj, i) => (
                <div
                  className={`${
                    messageObj.sender === user.id ? "sending" : "receiving"
                  }`}
                  key={i}
                >
                  <div
                    className={`sending_receiving_inner ${
                      messageObj.sender === user.id
                        ? "sending_inner"
                        : "receiving_inner"
                    }`}
                  >
                    {messageObj.text}
                  </div>
                </div>
              ))}
            {messagesLoading && (
              <PuffLoader className="messages_loading" color="#fff" size={30} />
            )}
          </div>
          <div className="chat_text_box">
            <input
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Send a message..."
            />
            {!loading && (
              <svg viewBox="0 0 512 512" onClick={sendMessage}>
                <path d="M498.1 5.6c10.1 7 15.4 19.1 13.5 31.2l-64 416c-1.5 9.7-7.4 18.2-16 23s-18.9 5.4-28 1.6L277.3 424.9l-40.1 74.5c-5.2 9.7-16.3 14.6-27 11.9S192 499 192 488V392c0-5.3 1.8-10.5 5.1-14.7L362.4 164.7c2.5-7.1-6.5-14.3-13-8.4L170.4 318.2l-32 28.9 0 0c-9.2 8.3-22.3 10.6-33.8 5.8l-85-35.4C8.4 312.8 .8 302.2 .1 290s5.5-23.7 16.1-29.8l448-256c10.7-6.1 23.9-5.5 34 1.4z" />
              </svg>
            )}
            {loading && <PuffLoader color="#fff" size={30} />}
          </div>
        </div>
      )}
    </div>
  );
}
