import "./style.css";
import { useSelector } from "react-redux";
import { useEffect, useRef, useState } from "react";
import { messenger } from "../../../functions/chat";
import PuffLoader from "react-spinners/PuffLoader";
const messages = [
  { id: 1, message: "Hi" },
  { id: 2, message: "Hello.." },
  { id: 1, message: "Whatsup" },
  { id: 2, message: "Fine man..what about you" },
  { id: 1, message: "doing good. where are you working now" },
  { id: 2, message: "im at bangalore,it company" },
  { id: 1, message: "oo nyc" },
  {
    id: 1,
    message: "can you text that old long message about development",
  },
  { id: 2, message: "ok wait.." },
  {
    id: 1,
    message:
      "Contrary to popular belief, Lorem Ipsum is not simply random text. It has roots in a piece of classical Latin literature from 45 BC, making it over 2000 years old. Richard McClintock, a Latin professor at Hampden-Sydney College in Virginia, looked up one of the more obscure Latin words, consectetur, from a Lorem Ipsum passage, and going through the cites of the word in classical literature, discovered the undoubtable source. Lorem Ipsum comes from sections 1.10.32 and 1.10.33 of de Finibus Bonorum et Malorum (The Extremes of Good and Evil) by Cicero, written in 45 BC. This book is a treatise on the theory of ethics, very popular during the Renaissance. The first line of Lorem Ipsum, Lorem ipsum dolor sit amet.., comes from a line in section 1.10.32 The standard chunk of Lorem Ipsum used since the 1500s is reproduced below for those interested. Sections 1.10.32 and 1.10.33 from de Finibus Bonorum et Malorum by Cicero are also reproduced in their exact original form, accompanied by English versions from the 1914 translation by H. Rackham.",
  },
];
export default function ChatBox({ selectedChat }) {
  const { user } = useSelector((state) => ({ ...state }));
  const [loading, setLoading] = useState(false);

  const messageContainerRef = useRef(null);
  const [message, setMessage] = useState("");
  const sendMessage = async () => {
    setLoading(true);
    console.log(selectedChat.id, message, user.token);
    await messenger(selectedChat.id, message, user.token);
    setLoading(false);
  };
  // useEffect(() => {
  //   messageContainerRef.current.scrollTop =
  //     messageContainerRef.current.scrollHeight;
  // }, []);
  return (
    <div className="chatBox">
      {!selectedChat.id && (
        <div className="noSelectedChat">
          <div>Select a chat to start messaging</div>
        </div>
      )}
      {selectedChat.id && (
        <div className="chat_box_wrap">
          <div className="chat_box_header">
            <img src={selectedChat.picture} alt="" />
            <h4>{`${selectedChat.first_name} ${selectedChat.last_name}`}</h4>
            <div>online</div>
          </div>
          <div
            ref={messageContainerRef}
            className="message_container scrollbar"
          >
            {messages.reverse().map((messageObj, i) => (
              <div
                className={`${
                  messageObj.id === user.id ? "sending" : "receiving"
                }`}
                key={i}
              >
                <div
                  className={`sending_receiving_inner ${
                    messageObj.id === user.id
                      ? "sending_inner"
                      : "receiving_inner"
                  }`}
                >
                  {messageObj.message}
                </div>
              </div>
            ))}
          </div>
          <div className="chat_text_box">
            <input
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Send a message..."
            />
            {!loading && (
              <svg viewBox="0 0 512 512" onClick={sendMessage}>
                <path d="M498.1 5.6c10.1 7 15.4 19.1 13.5 31.2l-64 416c-1.5 9.7-7.4 18.2-16 23s-18.9 5.4-28 1.6L277.3 424.9l-40.1 74.5c-5.2 9.7-16.3 14.6-27 11.9S192 499 192 488V392c0-5.3 1.8-10.5 5.1-14.7L362.4 164.7c2.5-7.1-6.5-14.3-13-8.4L170.4 318.2l-32 28.9 0 0c-9.2 8.3-22.3 10.6-33.8 5.8l-85-35.4C8.4 312.8 .8 302.2 .1 290s5.5-23.7 16.1-29.8l448-256c10.7-6.1 23.9-5.5 34 1.4z" />
              </svg>
            )}
            {loading && <PuffLoader color="#fff" size={30} />}
          </div>
        </div>
      )}
    </div>
  );
}
