import { Routes, Route } from "react-router-dom";
import Login from "./pages/login";
import Profile from "./pages/profile";
import Home from "./pages/home";
import LoggedInRoutes from "./routes/LoggedInRoutes";
import NonLoggedInRoutes from "./routes/NonLoggedInRoutes";
import { useReducer, useState } from "react";
import CreatePostPopup from "./components/createPostPopup";
import { useSelector } from "react-redux";
import axios from "axios";

function postFetchReducer(state, action) {
  switch (action.type) {
    case "REQUEST_STARTED":
      return { ...state, loading: true, error: "" };
    case "REQUEST_SUCCESS":
      return { ...state, loading: false, error: "", posts: action.payload };
    case "REQUEST_FAILURE":
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
}
function App() {
  const [createPostPopup, setCreatePostPopup] = useState(false);
  const [{ loading, error, posts }, dispatch] = useReducer(postFetchReducer, {
    loading: false,
    error: "",
    posts: [],
  });
  const { user } = useSelector((state) => ({ ...state }));

  const getAllPosts = () => {
    dispatch({ type: "REQUEST_STARTED" });
    axios.get("", {});
  };
  return (
    <div>
      {createPostPopup && (
        <CreatePostPopup user={user} setCreatePostPopup={setCreatePostPopup} />
      )}
      <Routes>
        <Route element={<LoggedInRoutes />}>
          <Route path="/profile" element={<Profile />} exact />
          <Route
            path="/"
            element={<Home setCreatePostPopup={setCreatePostPopup} />}
            exact
          />
        </Route>
        <Route element={<NonLoggedInRoutes />}>
          <Route path="/login" element={<Login />} exact />
        </Route>
      </Routes>
    </div>
  );
}

export default App;
